import React, { useState } from 'react';
import { Usuario } from './types/Usuario';
import UsuarioForm from './components/UsuarioForm';
import UsuarioList from './components/UsuarioList';

function App() {
  const [showForm, setShowForm] = useState(false);
  const [editingUsuario, setEditingUsuario] = useState<Usuario | undefined>();
  const [refreshKey, setRefreshKey] = useState(0);

  const handleNewUsuario = () => {
    setEditingUsuario(undefined);
    setShowForm(true);
  };

  const handleEditUsuario = (usuario: Usuario) => {
    setEditingUsuario(usuario);
    setShowForm(true);
  };

  const handleSaveUsuario = () => {
    setShowForm(false);
    setEditingUsuario(undefined);
    setRefreshKey(prev => prev + 1);
  };

  const handleCancelForm = () => {
    setShowForm(false);
    setEditingUsuario(undefined);
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-blue-600 text-white shadow-md">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-6">
            <h1 className="text-3xl font-bold">Sistema de Cadastro</h1>
            {!showForm && (
              <button
                onClick={handleNewUsuario}
                className="bg-white text-blue-600 px-4 py-2 rounded-md font-medium hover:bg-blue-50 transition-colors"
              >
                Novo Usu√°rio
              </button>
            )}
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {showForm ? (
          <div className="mb-8">
            <UsuarioForm
              usuario={editingUsuario}
              onSave={handleSaveUsuario}
              onCancel={handleCancelForm}
            />
          </div>
        ) : (
          <UsuarioList
            key={refreshKey}
            onEdit={handleEditUsuario}
            onRefresh={() => setRefreshKey(prev => prev + 1)}
          />
        )}
      </main>
    </div>
  );
}

export default App;
